window.appLoader=(function($){return(function(){var appLoader={show:show,hide:hide,options:{container:"body",zIndex:"auto",css:""}};return appLoader;function show(options){var $template=$("#app-loader");this._settings=_prepare_settings(options);if(!$template.length){var $container=$(this._settings.container);if($container.length){$container.append('<div id="app-loader" class="app-loader" style="z-index:'+this._settings.zIndex+";"+this._settings.css+'"><div class="loading"></div></div>');}else{console.log("appLoader: container must be an html selector!");}}}function hide(){var $template=$("#app-loader");if($template.length){$template.remove();}}function _prepare_settings(options){if(!options){options={};}return this._settings=$.extend({},appLoader.options,options);}})();})(jQuery);